mapper.update:
UPDATE BC_FAQ SET
FAQ_TITLE =#{faq_title},
FAQ_CONTENT =#{faq_content}
WHERE FAQ_NO =#{faq_no}

mapper.select:
SELECT * FROM BC_FAQ WHERE 1=1 AND FAQ_CONTENT LIKE CONCAT('%', #{searchKeyword}, '%')
AND FAQ_TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY FAQ_NO DESC LIMIT #{sizePerPage} OFFSET #{startList}
