mapper.select:
SELECT o.*, p.P_DESC, p.P_PRICE, p.P_IMG, row_number() OVER() AS rnum
FROM BC_ORDER AS o JOIN BC_PROD AS p ON o.P_NO = p.P_NO

	AND o.O_NO LIKE CONCAT('%', #{o_no}, '%')


	AND o.U_NAME LIKE CONCAT('%', #{u_name}, '%')


	AND o.O_DATE = #{o_date}


	AND o.O_STATE = #{o_state}

ORDER BY rnum DESC
LIMIT #{sizePerPage} OFFSET #{startList}

SELECT o.*, p.P_DESC, p.P_PRICE, p.P_IMG, row_number() over() as rnum
FROM BC_ORDER AS o JOIN BC_PROD as p on o.P_NO = p.P_NO

	AND o.O_NO LIKE CONCAT('%', #{o_no}, '%')


	AND o.U_NAME LIKE CONCAT('%', #{u_name}, '%')


	AND o.O_DATE = #{o_date}


	AND o.O_STATE = #{o_state}

GROUP BY O_NO
ORDER BY rnum DESC
LIMIT #{sizePerPage} OFFSET
#{startList}

SELECT COUNT(*) AS group_count FROM ( SELECT o.*, p.P_DESC, p.P_PRICE, p.P_IMG, ROW_NUMBER() OVER() AS rnum FROM BC_ORDER AS o JOIN BC_PROD AS p ON o.P_NO = p.P_NO
AND o.O_NO LIKE CONCAT('%', #{o_no}, '%')
AND o.U_NAME LIKE CONCAT('%', #{u_name}, '%')
AND o.O_DATE = #{o_date}
AND o.O_STATE = #{o_state}
GROUP BY O_NO ORDER BY rnum DESC ) AS subquery
